import React, 
    { 
        useState, 
        useEffect 
    } 
from 'react';
import { 
    Link, 
    useParams 
} from 'react-router-dom';


function Github()
{
    const {nomeUsuario} = useParams();

    const [ error, setError ] = useState(null);
    const [ isLoading, setIsLoading ] = useState(false);
    const [ items, setItems ] = useState([]);

    useEffect(() => {
        if(nomeUsuario) {
            setIsLoading(true)

            console.log(nomeUsuario);

            const URI = `https://api.github.com/user/${nomeUsuario}/repos`;

            fetch(URI, {mode: 'cors'})
                .then(res => res.json())
                .then( result => {
                    console.log(result)
                    setIsLoading(false)
                    setItems(result)
                })
                .catch(error => console.error)
        } else {
            setItems([]);
        }
    }, [nomeUsuario]);

    return(
        <>
            <div>
                <h1 className="identificador-cabecalho"> Github </h1>
                <p> Lista de usuário do Github. </p>
            </div>
            <div>
                <ul>
                    <li>
                        <Link to="/github/danielcsrs">Daniel</Link>
                    </li>
                    <li>
                        <Link to="/github/alvesdnelio">Nélio Souza Alves</Link>
                    </li>
                    <li>
                        <Link to="/github/yurigauermarques">Yuri Gauer Marques</Link>
                    </li>
                    <li>
                        <Link to="/github/HenriqueJoanoni">Henrique Joanoni</Link>
                    </li>
                    <li>
                        <Link to="/github/danielbackes">Daniel Backes</Link>
                    </li>
                </ul>

                {nomeUsuario && (
                    <p>Identificador da página: {nomeUsuario} </p> 
                )}

                {(isLoading) && <> Carregando... </>}

                {error && error}

                {items && (
                    <ul>
                        {items.map(repo => <li>{repo.name}</li>)}
                    </ul>
                )}
            </div>
        </>
    );
}

export default Github;
