import React, 
    { 
        useState, 
        useEffect 
    } 
from 'react';
import { 
    Link, 
    useParams 
} from 'react-router-dom';


function Github()
{
    const {nomeUsuario} = useParams();

    const [ error, setError ] = useState(null);
    const [ isLoading, setIsLoading ] = useState(false);
    const [ items, setItems ] = useState([]);

    useEffect(() => {
        if(nomeUsuario) {
            console.log(nomeUsuario);

            const URI = `https://api.github.com/users/${nomeUsuario}`;

            fetch(URI)
                .then(result => console.log(result))
                .catch(error => console.error)
        }
    }, []);

    return(
        <>
            <h1 className="identificador-cabecalho"> Github </h1>
            <p> Lista de usuário do Github. </p>
            <ul>
                <li>
                    <Link to="/github/danielcsrs">Daniel</Link>
                </li>
                <li>
                    <Link to="/github/yurigauermarques">Yuri Gauer Marques</Link>
                </li>
                <li>
                    <Link to="/github/HenriqueJoanoni">Henrique Joanoni</Link>
                </li>
                <li>
                    <Link to="/github/danielbackes">Daniel Backes</Link>
                </li>
            </ul>

            {nomeUsuario && (
                <p>Identificador da página: {nomeUsuario} </p> 
            )}

            {isLoading && <> Carregando... </>}
            {error && error}
        </>
    );
}

export default Github;
